CREATE TABLE `cyl_test` (
                            `job_id` varchar(64) NOT NULL COMMENT '任务id',
                            `job_name` varchar(80) NOT NULL DEFAULT '' COMMENT '任务名',
                            `execute_handler` varchar(60) NOT NULL DEFAULT '' COMMENT '执行器任务handler',
                            `job_type` int(1) NOT NULL DEFAULT '1' COMMENT '任务类型，1：导入，2：导出  3 转移',
                            `business_name` varchar(80) NOT NULL DEFAULT '' COMMENT '业务类型',
                            `job_status` int(11) NOT NULL DEFAULT '1' COMMENT '任务状态：1 未开始 2 排队中  3 进行中 4 暂停 5 完成  6 等待终止 7 终止 8 失败',
                            `available_status` tinyint(2) NOT NULL DEFAULT '0' COMMENT '可以运行的任务：1 是，2 false',
                            `queue_priority` int(11) NOT NULL DEFAULT '0' COMMENT '排队优先级,(数值越大,优先级越高)',
                            `queue_time` bigint(20) NOT NULL DEFAULT '0' COMMENT '排队时间戳(数值越小,优先级越高)',
                            `start_time` datetime DEFAULT NULL COMMENT '开始时间',
                            `start_running_time` datetime DEFAULT NULL COMMENT '运行开始时间',
                            `end_time` datetime DEFAULT NULL COMMENT '结束时间',
                            `total_running_time` bigint(20) NOT NULL DEFAULT '0' COMMENT '运行总耗时ms',
                            `total_time` bigint(20) NOT NULL DEFAULT '0' COMMENT '总耗时ms',
                            `host_name` varchar(64) NOT NULL DEFAULT '' COMMENT '主机名',
                            `ip` varchar(32) NOT NULL DEFAULT '' COMMENT 'ip',
                            `version` int(11) NOT NULL DEFAULT '0' COMMENT '版本号',
                            `company_id` int(11) NOT NULL DEFAULT '-1' COMMENT '公司id',
                            `user_id` int(11) NOT NULL COMMENT '用户ID',
                            `gmt_create` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
                            `gmt_modified` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '修改时间',
                            `run_num` int(11) NOT NULL DEFAULT '0',
                            `execute_thread_num` int(11) NOT NULL DEFAULT '0' COMMENT '执行的并发数',
                            `myjson` json DEFAULT NULL,
                            PRIMARY KEY (`job_id`),
                            KEY `idx_company_id_user_id` (`company_id`,`user_id`),
                            KEY `idx_available_status_job_status_job_type` (`available_status`,`job_status`,`job_type`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='任务表'

